mapper.select:
select *
from sh_room
where room_id = #{id}

select room_category_no as no, room_category_name as name, category_no as category, room_group_no as roomGroupNo
from sh_room_category
where room_category_no = #{value}

select *
from sh_room
where location_no = #{value}

select * 
from sh_room
where location_name = #{value}

select *
from sh_room
where room_category_no = #{value}

select amenity_type as type, amenity_description as description, room_category_no as roomCategory
from sh_room_amenity
where room_category_no = #{value}

select room_category_no as no, room_category_name as name, room_group_no as roomGroupNo, category_no as category
from sh_room_category
where room_group_no = #{value}

select *
from sh_room
where sh_room_name = #{value}

select room_category_no as no, room_category_name as name, category_no as category, room_group_no as roomGroupNo
from sh_room_category
where room_group_no = #{value}

select *
from sh_room_rev
where room_rev_no = #{value}
